package mobi.chy.map.globalpoi.util;

import java.util.Arrays;

/**
 * 点判断工具类，判断是否在中国
 *
 * Created by @author chengyong on 2018/3/1.
 */
public class LbsTool {

    private static final String CHINA_MAP = "";
    private static final String[] CC_NAME = {"AD","AE","AF","AG","AI","AL","AM","AO","AR","AT","AU","AZ","BB","BD","BE","BF","BG","BH","BI","BJ","BL","BM","BN","BO","BR","BS","BW","BY","BZ","CA","CF","CG","CH","CK","CL","CM","CN","CO","CR","CS","CU","CY","CZ","DE","DJ","DK","DO","DZ","EC","EE","EG","ES","ET","FI","FJ","FR","GA","GB","GD","GE","GF","GH","GI","GM","GN","GR","GT","GU","GY","HK","HN","HT","HU","ID","IE","IL","IN","IQ","IR","IS","IT","JM","JO","JP","KE","KG","KH","KP","KR","KT","KW","KZ","LA","LB","LC","LI","LK","LR","LS","LT","LU","LV","LY","MA","MC","MD","MG","ML","MM","MN","MO","MS","MT","MU","MV","MW","MX","MY","MZ","NA","NE","NG","NI","NL","NO","NP","NR","NZ","OM","PA","PE","PF","PG","PH","PK","PL","PR","PT","PY","QA","RO","RU","SA","SB","SC","SD","SE","SG","SI","SK","SL","SM","SN","SO","SR","ST","SV","SY","SZ","TD","TG","TH","TJ","TM","TN","TO","TR","TT","TW","TZ","UA","UG","US","UY","UZ","VC","VE","VN","YE","YU","ZA","ZM","ZR","ZW"};
    private static final String[] CC_CODE = {"376","971","93","1268","1264","355","374","244","54","43","61","994","1246","880","32","226","359","973","257","229","970","1441","673","591","55","1242","267","375","501","1","236","242","41","682","56","237","86","57","506","420","53","357","420","49","253","45","1890","213","593","372","20","34","251","358","679","33","241","44","1809","995","594","233","350","220","224","30","502","1671","592","852","504","509","36","62","353","972","91","964","98","354","39","1876","962","81","254","331","855","850","82","225","965","327","856","961","1758","423","94","231","266","370","352","371","218","212","377","373","261","223","95","976","853","1664","356","230","960","265","52","60","258","264","977","234","505","31","47","977","674","64","968","507","51","689","675","63","92","48","1787","351","595","974","40","7","966","677","248","249","46","65","386","421","232","378","221","252","597","239","503","963","268","235","228","66","992","993","216","676","90","1809","886","255","380","256","1","598","233","1784","58","84","967","381","27","260","243","263"};
    /**
     * 不在中国范围内
     *
     * @param lat 纬度
     * @param lng 经度
     * @return boolean
     */
    public static boolean isInChina(double lat, double lng) {
        //查表法
        int coorX = (int)((lng - 73.0D) / 0.5D);
        int coorY = (int)((lat - 3.5D) / 0.5D);
        if(coorY >= 0 && coorY < 101 && coorX >= 0 && coorX < 124) {
            try {
                coorY = 124 * coorY + coorX;
                return CHINA_MAP.charAt(coorY) == 49;
            } catch (Throwable throwable) {
                return true;
            }
        } else {
            return false;
        }
    }

    /**
     * 校验地址是否合法
     *
     * @param lat 纬度
     * @param lng 经度
     * @return boolean
     */
    public static boolean isVerifyPass(double lat, double lng) {
        if (lat == 0 && lng == 0) {
            return false;
        }
        return !(lat < -85 || lat > 85 || lng < -180 || lng > 180);
    }

    public static String getCountryCode(String cc){
        try {
            return CC_CODE[Arrays.binarySearch(CC_NAME, cc)];
        } catch (Exception e) {
            return "0";
        }
    }
}